mapper.select:
select ot.*,c.formulaId
from ordertab ot,car c,parkingtime pt
where ot.timeId=pt.timeId and pt.carId=c.carId and ot.timeId=#{timeId};

select o.orderId,c.licenseNum,o.beginTime,o.endTime,o.totalTime,o.money
from car c,parkingtime pt,ordertab o
where c.carId = pt.carId and pt.timeId = o.timeId and c.driverId = #{driverId};
